import React from "react";
import "./TodoItem.scss";
import { deleteTodo } from "../../../../actions/todoAction";
import { useDispatch } from "react-redux";

function TodoItem(props) {
    const dispatch = useDispatch();
    const { status, name, date } = props.item;
    const { index } = props;
    const handleDeleteTodo = (idx) => {
        const action = deleteTodo(idx);
        dispatch(action);
    };
    return (
        <div className="todo-item" index={index}>
            <input type="checkbox" onClick={handleDeleteTodo(index)} />
            <div className="status">{status ? "true" : "false"}</div>
            <div className="name">{name}</div>
            <div className="date">{date}</div>
        </div>
    );
}

export default TodoItem;
