const initialState = {
    todo: [],

    done: [],
};

const todoReducer = (state = initialState, action) => {
    switch (action.type) {
        case "ADD_TODO": {
            return { todo: [...state.todo, action.payload] };
        }
        case "DELETE_TODO": {
            console.log(state.done);
            return {
                done: [...state.done, state.todo[action.payload]],
                todo: [
                    ...state.todo.slice(0, action.payload),
                    ...state.todo.slice(action.payload + 1, state.todo.length),
                ],
            };
        }
        default:
            return state;
    }
};

export default todoReducer;
