const initialState = {
    todo: [],
};
const todoReducer = (state = initialState, action) => {
    switch (action.type) {
        case "ADD_TODO": {
            const newArr = [...state.tood];
            newArr.push(action.payload);
            return newArr;
        }
        case "DELETE_TODO": {
            return state;
        }
        default:
            return state;
    }
};

export default todoReducer;
