const initialState = {
    todo: [],
};
const todoReducer = (state = initialState, action) => {
    switch (action.type) {
        case "ADD_TODO": {
            return [...state, action.payload];
        }
        case "DELETE_TODO": {
            return state;
        }
        default:
            return state;
    }
};

export default todoReducer;
