const initialState = {
    todo: [],
    done: [],
};

const todoReducer = (state = initialState, action) => {
    switch (action.type) {
        case "ADD_TODO": {
            return { state, todo: [...state.todo, action.payload] };
        }
        case "DELETE_TODO": {
            return {
                todo: [
                    ...state.todo.slice(0, action.payload),
                    ...state.todo.slice(action.payload + 1, state.todo.length),
                ],
            };
        }
        case "DONE_TODO":{
            const newArr = [...state.todo];
            console.log(newArr[action.payload]);
            // todo: Object.assign({}, state.todo, {status: true})
            return state;
        }
        default:
            return state;
    }
};

export default todoReducer;
