const initialState = {
    todo: [],
    done: [],
};

const todoReducer = (state = initialState, action) => {
    switch (action.type) {
        case "ADD_TODO": {
            return { state, todo: [action.payload, ...state.todo] };
        }
        case "DELETE_TODO": {
            return {
                todo: [
                    ...state.todo.slice(0, action.payload),
                    ...state.todo.slice(action.payload + 1, state.todo.length),
                ],
            };
        }
        case "DONE_TODO": {
            const newArr = [...state.todo];
            const item = Object.assign({}, newArr[action.payload], {
                status: true,
            });
            newArr[action.payload] = item;
            return {
                todo: newArr,
            };
        }
        case "UPDATE_TODO": {
            const newArr = [...state.todo];
            const item = Object.assign({}, newArr[action.payload], {
                status: false,
            });
            newArr[action.payload] = item;
            return {
                todo: newArr,
            };
        }
        default:
            return state;
    }
};

export default todoReducer;
